import React, { Component } from 'react';

export class TechnologyPage extends Component {
    state = { 
        info: {}
    }

    componentDidMount = () => {
        const {info} = this.props.location.state;
        this.setState({info});
        document.title = 'AGT - ' + info.title;
    }

    styles = {
        header: {
            fontSize: 60,
            color: 'red'
        },

        body:{
            fontSize: 20
        }
    }

    formatBody = () => {
        var bodyText = [];
        console.log(this.state.info['body'])
        for(let i=0; i<this.state.info['body'].length; i++) {
            bodyText.push(<p class="subheading" style={this.styles.body}>{this.state.info['body'][i]}</p>)
        }
        return bodyText;
    }

    render() { 
        return ( 
            <React.Fragment>
            <div class="container border border-grey">
                <div class="text-center mt-4">
                    <h1 class="heading" style={this.styles.header}>{this.state.info['title']}</h1>
                </div>
                <div class="text-left ml-5 mr-5 mt-4">
                    {this.formatBody()}
                </div>
            </div>
            </React.Fragment>
        );
    }
}
