import React, { Component } from 'react';

export class NewsPage extends Component {
    state = { 
        info: {}
    }

    componentDidMount = () => {
        const {info} = this.props.location.state;
        this.setState({info});
    }

    render() { 
        return ( 
            <div class="text-center">
                <h1 class="display-1">{this.state.info['title']}</h1>
                <p>{this.state.info['text']}</p>
            </div>
        );
    }
}
