import React, { Component } from 'react';
import {TechnologyItem} from '../components/techItem.js';
import {PageNav} from '../components/pageNav.js';
import {Footer} from '../components/footer.js';
import {Link} from 'react-router-dom';
import {pub1} from '..../public/P01-SEG2014_Hu_BTFWI.pdf';

export class Technology extends Component {
    state = { 
        // just as in news.js, id corresponds to the number displayed in url

        // format must be either 'list' or 'text' exactly:
        // list: use key list equal to an array of strings to be displayed in a vertical, bulleted list under the card title
        // text: the entire string will be displayed and wrap around per line according to the dimensions of the containing card

        // the body key corresponds to the text that will appear in each instance page (after clicking more info on a card)
        // any additional keys can be accessed in src/components/techItem.js using this.props.info.keyName or this.props.info['keyName']
        technology: [
            { format: 'list', id: 1, title: '5-D INTERPOLATION', list: ['Global-Local-Hybrid Multi-resolution approach', 'Anti-leakage and anti-aliasing', 'Fills large data gaps'], body: ['AGT’s efficient global-local-hybrid 5D (GLH-5D) seismic data interpolation algorithm, based on the principle of multi-resolution analysis, poses the interpolation as an inverse problem and solves it in an iterative manner. In the first iteration, a low resolution interpolation function is built to roughly approximate the given dataset to resolve the low wavenumber components. With the iteration proceeds, more and more fine features of the seismic data are brought into the interpolation procedure through the gradually refined control lattices, while the interpolation error reduces accordingly. With this iterative multiresolution fashion, the broadband spatial spectral information of the seismic data is reconstructed efficiently.',
            'Compared with the widely used FFT-based method, there is no binning procedure needed in this algorithm and there is no regularization parameter required, which reduces the uncertainty and bias caused by user interaction and experiences. Actually, the regularization is natively and automatically embedded in the iterative dyadic control lattice strategy in this GLH-5D algorithm. Because this algorithm employs a local interpolation operator with the interpolation stencil length of 4, it is much more efficient than the global interpolation methods, especially the DFT-based method. On the other hand, the global information is taken into account through the iterative multi-resolution interpolation strategy. Furthermore, the local interpolation operator employed by this hybrid interpolation algorithm is an optimally windowed Sinc interpolation, whose spectral properties are precisely controlled to guarantee its anti-aliasing performance. Another important feature of this algorithm is that the initial control lattice resolution can be determined by the frequency bandwidth of the seismic data and the corresponding velocity model. The final control lattice is determined by the predefined interpolation error. These facts further reduce the computational cost and make this algorithm completely automatic. For these reasons, the GLH-5D method is a powerful tool for seismic data processing.']},
            /*{ format: 'list', category: 'tech', id: 10, title: 'DIFFRACTION IMAGING', list: ['Double stage reflection energy suppression'], body: []},
            { format: 'list', category: 'tech', id: 9, title: 'FORWARD MODELING', list: ['Finite Element and Finite Difference', 'Surface/Seafloor topography', 'Variable gridding', 'Full physics (Acoustic/Elastic, ISO/VTI/TTI/Orthorhombic, Q)', 'VSP/Sea floor multi-component recording'], body: 'Body text to be added.'},
            { format: 'list', category: 'tech', id: 8, title: '3D TOMOGRAPHIC VELOCITY MODEL BUILDING WITH Q, ORTHORHOMBIC', list: ['High-resolution tomography for small velocity anomalies', 'Superior fault definition and improved image', 'Global automatic residual depth move-out picking', 'Geology structural conformed inversion', 'Sub-salt velocity update with RTM dense angle gathers or shot index gathers'], body: 'Body text to be added.'},
            { format: 'list', category: 'tech', id: 7, title: 'PREPROCESSING', list: ['Internal time processing package along with Paradigm/Echos'], body: 'Body text to be added.'},
            { format: 'list', category: 'tech', id: 6, title: '3D KIRCHHOFF DEPTH & TIME MIGRATION', list: ['Q, HTI/VTI, Orthorhombic', 'Designed for large 3D datasets and high resolution imaging', 'Large scale seismic modeling capability'], body: 'Body text to be added.'},
            { format: 'list', category: 'tech', id: 5, title: 'FULL WAVEFORM INVERSION (PATENTED)', list: ['Beat-Tone FWI algorithm that does not require low-frequency data', 'Insensitive to source estimation error and data measurement error', 'Fast convergence rate (efficient computation of second-order derivative) ', 'Highly automatic algorithm, minimum user interaction', 'Optimized workflows designed for various categories of seismic data'], body: 'Body text to be added.'},
            { format: 'list', category: 'tech', id: 4, title: '3D RTM, QRTM & RTM ANGLE GATHERS (ISO/VTI/TTI)', list: ['AVO compliant offset and angle gathers, Finite-difference Q-RTM (patent pending) which is 5x faster than traditional methods', 'Affordable high frequency (e.g. 50Hz) 3D RTM angle gather', 'Output every location in offset or angle domain', 'Azimuthal Imaging'], body: 'Body text to be added.'},
        */
        
        ],
        publications:[
            {title: '(P01) Hu, Wenyi, 2014. FWI without low frequency data-beat tone inversion. In SEG Technical Program Expanded Abstracts 2014 (pp. 1116-1120). Society of Exploration Geophysicists.', 
            link: pub1},
        ], // publications
        numTechItems: 8, // number of tech entries to be displayed per page
        numPubItems: 10,
        page: 0,
        title: 'TECHNOLOGIES',
        filter: 'tech'
    }

    componentDidMount() {
        document.title = 'AGT - Technology';
    }
    
    // convert numTechItems at a time news entries into NewsItem components
    formatTech = () => {
        var techJSX = [];
        let i = this.state.page * this.state.numTechItems;
        while(i<this.state.technology.length && i<this.state.page*this.state.numTechItems + 8){
            let dict = this.state.technology[i];
            techJSX.push(<TechnologyItem info={dict}/>)
            i++;
        }
        if(techJSX.length > 0)
            return techJSX;
        else
            return <span class="txt subheading ml-2 mt-2">Nothing on this page.</span>
    }

    formatPub = () => {
        var pubJSX = [];
        let i = this.state.page * this.state.numPubItems;
        while(i<this.state.publications.length && i<this.state.page*this.state.numPubItems + 8){
            let dict = this.state.publications[i];
            pubJSX.push(<li><a href={pub1}>{dict['title']}</a></li>)
            i++;
        }
        var result = <ol>{pubJSX}</ol>
        if(pubJSX.length > 0)
            return result;
        else
            return <span class="txt subheading ml-2 mt-2">Nothing on this page.</span>
    }

    incrementPage = () => {
        const page = this.state.page + 1;
        this.setState({page});
    }

    incrementPageTwice = () => {
        const page = this.state.page + 2;
        this.setState({page});
    }
    
    decrementPage = () => {
        const page = this.state.page - 1;
        this.setState({page});
    }
    
    goFirstPage = () => {
        const page = 0;
        this.setState({page});
    }

    goLastPage = () => {
        const page = Math.ceil(this.state.technology.length/this.state.numTechItems) - 1;
        this.setState({page});
    }

    reset = () => {
        this.setState({technology: this.state.publications});
    }

    applyFilter = (num) => {
        var newTitle = '';
        var newFilter = '';
        if(num==0) {
            newTitle = 'PUBLICATIONS';
            newFilter = 'pub';
        }
        else if(num==1) {
            newTitle = 'TECHNOLOGIES';
            newFilter = 'tech';
        }
        this.setState({title: newTitle, filter: newFilter, page: 0})
    }

    render() { 
        var techFormatted = this.formatTech();
        var pubFormatted = this.formatPub();
        return ( 
            <div>
                <a href={pub1} target="_blank">CLICK</a>
            <div class="container-fluid border border-grey" style={{width: '80vw', minWidth: 700}}>
                <div class="row">
                    <div class="col-2 border-right text-center" style={{minWidth: 150}}>
                        <br/>
                       <span class="txt heading" style={{fontSize: 25, fontWeight: 'bold', color: '#1cb7e6'}}>Filter by:</span>
                       <br/>
                       <Link class="subheading" onClick={() => this.applyFilter(0)} style={{fontSize: 20, color: 'black'}}>Publications</Link><br/>
                       <Link class="subheading" onClick={() => this.applyFilter(1)} style={{fontSize: 20, color: 'black'}}>Technologies</Link><br/>
                    </div>
                    <div class="col-8">
                        <h1 class="heading ml-2 mt-1" style={{fontSize: 35, color: '#585c5e'}}>{this.state.title}</h1>
                        <div class="container-fluid" style={{width: '65vw'}}>
                            <div class="row">
                                {this.state.filter === 'tech' ?
                                <React.Fragment>
                                <div class="col padding-0" style={{width: '28vw'}}>
                                    {techFormatted.length >= 1 ? techFormatted[0] : null}
                                    {techFormatted.length >= 3 ? techFormatted[2] : null}
                                    {techFormatted.length >= 5 ? techFormatted[4] : null}
                                    {techFormatted.length >= 7 ? techFormatted[6] : null}
                                </div>
                                <div class="col padding-0">
                                    {techFormatted.length >= 2 ? techFormatted[1] : null}
                                    {techFormatted.length >= 4 ? techFormatted[3] : null}
                                    {techFormatted.length >= 6 ? techFormatted[5] : null}
                                    {techFormatted.length >= 8 ? techFormatted[7] : null}
                                </div>
                                </React.Fragment> :
                                <React.Fragment>
                                    {pubFormatted}
                                </React.Fragment>
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div class="d-flex justify-content-center">
                <PageNav decrementPage={this.decrementPage} 
                        incrementPage={this.incrementPage}
                        goFirstPage={this.goFirstPage}
                        goLastPage={this.goLastPage}
                        page={this.state.page}
                        incrementPageTwice={this.incrementPageTwice}
                        lastPage={Math.ceil(this.state.technology.length/this.state.numTechItems)}/>
            </div>
            <Footer min={700}/>
            </div>
        );
    }
}
