import React, { Component } from 'react';
import {ServiceItem} from '../components/serviceItem.js';
import {PageNav} from '../components/pageNav.js';
import {Footer} from '../components/footer.js';
import {Link} from 'react-router-dom';

export class Services extends Component {
    state = { 
        services: [
            { category: 'service', id: 3, title: 'Research & Development', text: '40% of all capital is commited to R&D. We deliver customer focused results - our technology roadmap is adaptable. Rapid Technology Implementation - i.e. AVO Compliant RTM Gathers, Q. Nimble; technology roadmap adaptable to clients\' needs.'},
            { category: 'service', id: 2, title: 'Migration & Velocity Model Building', text: '2D/3D Time, 2D/3D Depth, Land/Marine (NAZ, WAZ, Broadband, Nodal, etc.)'},
            { category: 'service', id: 1, title: 'Seismic Imaging Service', text: 'AGT provides advanced, best-in-class seismic imaging services to Oil and Gas Companies.'}
        ],
        servicesReset:[
            { category: 'service', id: 18, title: 'Service 1', text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'},
            { category: 'tech', id: 18, title: 'Service 1', text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'},
        ], // a copy of the entirety of news to be saved every time news is modified by a function call
        numServiceItems: 6, // number of news entries to be displayed per page
        page: 0,
        title: 'SERVICES AND TECHNOLOGIES'
    }
    
    // convert numServiceItems at a time news entries into NewsItem components
    formatService = () => {
        var serviceJSX = [];
        let i = this.state.page * this.state.numServiceItems;
        while(i<this.state.services.length && i<this.state.page*this.state.numServiceItems + 6){
            let dict = this.state.services[i];
            serviceJSX.push(<ServiceItem info={dict}/>)
            i++;
        }
        return serviceJSX;
    }

    incrementPage = () => {
        const page = this.state.page + 1;
        this.setState({page});
    }

    incrementPageTwice = () => {
        const page = this.state.page + 2;
        this.setState({page});
    }
    
    decrementPage = () => {
        const page = this.state.page - 1;
        this.setState({page});
    }
    
    goFirstPage = () => {
        const page = 0;
        this.setState({page});
    }

    goLastPage = () => {
        const page = Math.ceil(this.state.services.length/this.state.numServiceItems) - 1;
        this.setState({page});
    }

    reset = () => {
        this.setState({services: this.state.servicesReset});
    }

    applyFilter = (num) => {
        var newTitle = '';
        var newList = [];
        if(num==0) {
            newTitle = 'SERVICES';
            newList = this.state.servicesReset.filter(o=>{return o['category'] == 'service'});
        }
        else if(num==1) {
            newTitle = 'TECHNOLOGIES';
            newList = this.state.servicesReset.filter(o=>{return o['category'] == 'tech'});
        }
        else {
            newTitle = 'SERVICES AND TECHNOLOGIES';
            newList = this.state.servicesReset;
        }
        this.setState({title: newTitle, services: newList})
    }

    render() { 
        var serviceFormatted = this.formatService();
        return ( 
            <div style={{width: '100vw'}}>
            <div class="container border border-grey">
                <div class="row">
                    <div class="col-2 border-right text-center">
                        <br/>
                       <span class="txt heading" style={{fontSize: 20, fontWeight: 'bold', color: '#1cb7e6'}}>Filter by:</span>
                       <br/>
                       <Link class="subheading" onClick={() => this.applyFilter(0)} style={{fontSize: 20, color: 'black'}}>Services</Link><br/>
                       <Link class="subheading" onClick={() => this.applyFilter(1)} style={{fontSize: 20, color: 'black'}}>Technologies</Link><br/>
                       <Link class="subheading" onClick={() => this.applyFilter(2)} style={{fontSize: 20, color: 'black'}}>Both</Link><br/>
                    </div>
                    <div class="col-8">
                        <h1 class="heading ml-2 mt-1" style={{fontSize: 35, color: '#585c5e'}}>{this.state.title}</h1>
                        {serviceFormatted}
                    </div>
                </div>
            </div>
            <div class="d-flex justify-content-center">
                <PageNav decrementPage={this.decrementPage} 
                        incrementPage={this.incrementPage}
                        goFirstPage={this.goFirstPage}
                        goLastPage={this.goLastPage}
                        page={this.state.page}
                        incrementPageTwice={this.incrementPageTwice}
                        lastPage={Math.ceil(this.state.services.length/this.state.numServiceItems)}/>
            </div>
            <Footer/>
            </div>
        );
    }
}
