    // convert numServiceItems at a time news entries into NewsItem components
    formatService = () => {
        var serviceJSX = [];
        let i = this.state.page * this.state.numServiceItems;
        while(i<this.state.news.length && i<this.state.page*this.state.numServiceItems + 6){
            let dict = this.state.news[i];
            serviceJSX.push(<NewsItem title={dict['title']} text={dict['text']}/>)
            i++;
        }
        return serviceJSX;
    }

    incrementPage = () => {
        const page = this.state.page + 1;
        this.setState({page});
    }

    incrementPageTwice = () => {
        const page = this.state.page + 2;
        this.setState({page});
    }

    decrementPage = () => {
        const page = this.state.page - 1;
        this.setState({page});
    }

    goFirstPage = () => {
        const page = 0;
        this.setState({page});
    }

    goLastPage = () => {
        const page = Math.ceil(this.state.news.length/this.state.numServiceItems) - 1;
        this.setState({page});
    }

    applyFilter = (year) => {
        var news = this.state.newsReset.filter(o => {return o['year'] === year});
        this.setState({news});
    }

    reset = () => {
        this.setState({news: this.state.newsReset});
    }

    render() { 
        var serviceFormatted = this.formatService();
        return ( 
            <React.Fragment>
            <div class="container">
                <div class="row">
                    <div class="col-4 border-right">
                        <br/>
                        <Link onClick={this.reset} style={{fontSize: 20, color: 'black'}}>All News</Link><br/>
                        <Link onClick={() => this.applyFilter(2019)} style={{fontSize: 20, color: 'black'}}>2019-2020</Link><br/>
                        <Link onClick={() => this.applyFilter(2018)} style={{fontSize: 20, color: 'black'}}>2018-2019</Link><br/>
                        <Link onClick={() => this.applyFilter(2017)} style={{fontSize: 20, color: 'black'}}>2017-2018</Link><br/>
                        <Link onClick={() => this.applyFilter(2016)} style={{fontSize: 20, color: 'black'}}>2016-2017</Link><br/>
                    </div>
                    <div class="col-8">
                        {newsFormatted}
                    </div>
                </div>
            </div>
            <div class="d-flex justify-content-center">
                <PageNav decrementPage={this.decrementPage} 
                        incrementPage={this.incrementPage}
                        goFirstPage={this.goFirstPage}
                        goLastPage={this.goLastPage}
                        page={this.state.page}
                        incrementPageTwice={this.incrementPageTwice}
                        lastPage={Math.ceil(this.state.news.length/this.state.numServiceItems)}/>
            </div>
            </React.Fragment>
        );
    }
